import numpy as npfrom utils.helpers import *from utils.implementations import reg_logistic_regression# Parametersdegree = 3gamma = 0.5lambda_ = 0.16# Import datatrain, test, cols = import_data(path = "/data/")# Prepare train and test dataidx_id = np.where(cols=="Id")[0][0]idx_pred = np.where(cols=="Prediction")[0][0]tx_train = np.delete(train, [idx_id, idx_pred], axis=1)y_train = train[:,idx_pred].copy()tx_test = np.delete(test, [idx_id, idx_pred], axis=1)tx_train, mean, nan_cols = prepare_features(tx_train, degree)tx_test, _, _ = prepare_features(tx_test, degree, mean)# Train modelw, _ = reg_logistic_regression(y_train, tx_train, lambda_, gamma)# Save oos predictionsids = test[:,idx_id].copy()y_pred = predict_reg_log_regression(tx_test, w)y_pred[y_pred==0] = -1y_pred[y_pred==1] = 1create_csv_submission(ids, y_pred, "/scripts/submission.csv")